        return developer.ServiceExtensionResponse.result(
          json.encode({
            'status': 'success',
            'hasPosition': true,
            'x': position.dx,
            'y': position.dy,
          }),
        );
      } catch (e, stackTrace) {
                        return developer.ServiceExtensionResponse.error(
          developer.ServiceExtensionResponse.extensionError,
          'Failed to get cursor position: $e',
        );
      }
    },
  );

  }

